import React, { useState } from "react";
import { Link } from "react-router-dom";

const Login = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);

  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        backgroundColor: isDarkMode ? "#121212" : "#F4F7F4",
        transition: "all 0.3s",
      }}
    >
      <style>
        {`
          .login-container {
            background-color: ${isDarkMode ? "#1E1E1E" : "#fff"};
            padding: 50px;
            border-radius: 15px;
            box-shadow: 0 8px 20px rgba(0,0,0,0.3);
            width: 100%;
            max-width: 400px;
            transition: all 0.3s;
          }

          .login-container h2 {
            text-align: center;
            margin-bottom: 30px;
            color: ${isDarkMode ? "#f1f1f1" : "#0B3D02"};
          }

          .login-container input {
            width: 100%;
            padding: 12px 15px;
            margin: 10px 0;
            border: 1px solid #0B3D02;
            border-radius: 8px;
            font-size: 16px;
            outline: none;
            transition: all 0.3s;
          }

          .login-container input:focus {
            border-color: #1E8449;
            box-shadow: 0 0 5px #1E8449;
          }

          .login-container button {
            width: 100%;
            padding: 12px;
            margin-top: 20px;
            background-color: #145A32;
            color: white;
            border: none;
            border-radius: 8px;
            font-size: 18px;
            cursor: pointer;
            font-weight: bold;
            transition: all 0.3s;
          }

          .login-container button:hover {
            background-color: #1E8449;
          }

          .toggle-mode {
            margin-top: 15px;
            text-align: center;
            cursor: pointer;
            font-weight: bold;
            color: ${isDarkMode ? "#FFA500" : "#145A32"};
          }

          .login-footer {
            margin-top: 20px;
            text-align: center;
            font-size: 14px;
            color: ${isDarkMode ? "#f1f1f1" : "#222"};
          }

          .login-footer a {
            color: #1E8449;
            text-decoration: none;
            font-weight: bold;
          }

          .login-footer a:hover {
            text-decoration: underline;
          }
        `}
      </style>

      <div className="login-container">
        <h2>Login</h2>
        <input type="email" placeholder="Email" />
        <input type="password" placeholder="Password" />
        <button>Login</button>
        <div className="toggle-mode" onClick={toggleDarkMode}>
          {isDarkMode ? "Switch to Light Mode" : "Switch to Dark Mode"}
        </div>
        <div className="login-footer">
          Don't have an account?{" "}
          <Link to="/signup">Sign Up</Link>
        </div>
      </div>
    </div>
  );
};

export default Login;
