import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./CommonUI";
import "../css/admindashboard.css";
import AdminManage from "./AdminManage";
import { useNavigate } from 'react-router-dom';

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [searchTerm, setSearchTerm] = useState(""); // âœ… for user search

  // load authenticated user from /api/auth/me if token present
  const [authUser, setAuthUser] = React.useState(null);
  const token = typeof window !== 'undefined' ? localStorage.getItem('token') : null;
  const isLoggedIn = !!token;

  // fallback: read user object from localStorage (saved at login)
  const storedLocalUser = typeof window !== 'undefined' ? localStorage.getItem('user') : null;
  const parsedLocalUser = storedLocalUser ? JSON.parse(storedLocalUser) : null;

  React.useEffect(() => {
    let mounted = true;
    const fetchMe = async () => {
      if (!token) return;
      try {
        const res = await fetch('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) {
          console.warn('Could not fetch /me', res.status);
          return;
        }
        const data = await res.json();
        if (mounted && data && data.user) setAuthUser(data.user);
      } catch (e) {
        console.error('fetchMe error', e);
      }
    };
    fetchMe();
    return () => (mounted = false);
  }, [token]);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);
  const navigate = useNavigate();

  // State for real data
  const [totalUsers, setTotalUsers] = useState(0);
  const [totalQuestions, setTotalQuestions] = useState(0);

  React.useEffect(() => {
    // Fetch total students
    fetch('http://localhost:5000/api/users/count/students')
      .then(res => res.json())
      .then(data => setTotalUsers(data.count || 0))
      .catch(() => setTotalUsers(0));
    // Fetch total questions
    fetch('http://localhost:5000/api/questions/count')
      .then(res => res.json())
      .then(data => setTotalQuestions(data.count || 0))
      .catch(() => setTotalQuestions(0));
  }, []);

  const [questions, setQuestions] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [editingQuestion, setEditingQuestion] = useState(null);

  // Fetch questions from backend
  const fetchQuestions = async () => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch('http://localhost:5000/api/questions');
      if (!res.ok) {
        throw new Error('Failed to fetch questions');
      }
      const data = await res.json();
      setQuestions(data);
    } catch (err) {
      console.error('Error fetching questions:', err);
      setError('Failed to load questions. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, []);

  // Handle delete question
  const handleDelete = async (id) => {
    if (!window.confirm('Are you sure you want to delete this question?')) {
      return;
    }

    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/questions/${id}`, {
        method: 'DELETE',
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (!res.ok) {
        throw new Error('Failed to delete question');
      }

      // Refresh questions list
      fetchQuestions();
      alert('Question deleted successfully');
    } catch (err) {
      console.error('Error deleting question:', err);
      alert('Failed to delete question. Please try again.');
    }
  };

  // Handle edit question
  const handleEdit = (question) => {
    setEditingQuestion(question);
  };

  // Handle update question
  const handleUpdate = async (e) => {
    e.preventDefault();
    
    try {
      const token = localStorage.getItem('token');
      const res = await fetch(`http://localhost:5000/api/questions/${editingQuestion._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          Authorization: `Bearer ${token}`
        },
        body: JSON.stringify({
          text: editingQuestion.text,
          options: editingQuestion.options,
          correctAnswer: editingQuestion.correctAnswer,
          category: editingQuestion.category,
          difficulty: editingQuestion.difficulty,
          explanation: editingQuestion.explanation
        })
      });

      if (!res.ok) {
        throw new Error('Failed to update question');
      }

      // Refresh questions list and close edit modal
      setEditingQuestion(null);
      fetchQuestions();
      alert('Question updated successfully');
    } catch (err) {
      console.error('Error updating question:', err);
      alert('Failed to update question. Please try again.');
    }
  };

  const dummyUsers = [
    { id: 1, name: "John Doe", email: "john@example.com", role: "Student" },
    { id: 2, name: "Jane Smith", email: "jane@example.com", role: "Student" },
    { id: 3, name: "Alice Johnson", email: "alice@example.com", role: "Student" },
    { id: 4, name: "Bob Brown", email: "bob@example.com", role: "Student" },
    { id: 5, name: "Charlie Davis", email: "charlie@example.com", role: "Student" },
  ];

  // âœ… Filter users based on search
  const filteredUsers = dummyUsers.filter(
    (user) =>
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="admin-dashboard">
      {/* Navbar */}
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={isLoggedIn}
      />

      <div className="main-section">
        {/* Sidebar */}
        <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? "Close" : "â‰¡"}
          </button>

          {sidebarOpen && (
            <div className="sidebar-content">
              <h4>Profile</h4>
              <p><strong>Name:</strong> {authUser ? authUser.name : (parsedLocalUser ? parsedLocalUser.name : 'Admin Name')}</p>
              <p><strong>Email:</strong> {authUser ? authUser.email : (parsedLocalUser ? parsedLocalUser.email : 'admin@example.com')}</p>
              <p><strong>Role:</strong> {authUser ? authUser.role : (parsedLocalUser ? parsedLocalUser.role : 'Admin')}</p>
              <button
                className="manage-admin-btn"
                onClick={() => navigate('/AdminManage')}
              >
                MANAGE ADMIN
              </button>
              <button
                className="add-question-btn"
                style={{ marginTop: '10px', backgroundColor: '#145A32', color: 'white', padding: '8px 12px', borderRadius: 6, border: 'none' }}
                onClick={() => navigate('/add-question')}
              >
                ADD QUESTION
              </button>
            </div>
          )}
        </div>

        {/* Dashboard Content */}
        <div className="dashboard-content">
          <h1>Admin Dashboard</h1>
          <p>Welcome, Admin! Here you can manage users, view reports, and handle system settings.</p>

          {/* Overview */}
          <section className="overview-section">
            <h2>Overview Dashboard</h2>
            <div className="overview-cards">
              <div className="card">
                <h3>Total Users Registered</h3>
                <p>{totalUsers}</p>
              </div>
              <div className="card">
                <h3>Total Questions</h3>
                <p>{totalQuestions}</p>
              </div>
            </div>
          </section>

          {/* Question Management */}
          <section className="management-section">
            <h2>Question Management</h2>
            <div className="table-wrapper">
              <table className="question-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Question</th>
                    <th>Option A</th>
                    <th>Option B</th>
                    <th>Option C</th>
                    <th>Option D</th>
                    <th>Correct</th>
                    <th>Category</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {loading ? (
                    <tr>
                      <td colSpan="10" style={{ textAlign: "center", padding: "20px" }}>
                        Loading questions...
                      </td>
                    </tr>
                  ) : error ? (
                    <tr>
                      <td colSpan="10" style={{ textAlign: "center", padding: "20px", color: "red" }}>
                        {error}
                      </td>
                    </tr>
                  ) : questions.length === 0 ? (
                    <tr>
                      <td colSpan="10" style={{ textAlign: "center", padding: "20px" }}>
                        No questions available. Add some questions using the Add Question section.
                      </td>
                    </tr>
                  ) : (
                    questions.map((q, index) => (
                      <tr key={q._id}>
                        <td>{index + 1}</td>
                        <td>{q.text}</td>
                        <td>{q.options[0]}</td>
                        <td>{q.options[1]}</td>
                        <td>{q.options[2]}</td>
                        <td>{q.options[3]}</td>
                        <td>{q.correctAnswer}</td>
                        <td>{q.category}</td>
                        <td><button className="edit-btn">Edit</button></td>
                        <td><button className="delete-btn">Delete</button></td>
                      </tr>
                    ))
                  )}
                </tbody>
              </table>
            </div>
          </section>

          {/* ðŸ‘¥ User Management */}
          <section className="management-section" style={{ marginTop: "40px" }}>
            <h2>User Management</h2>

            {/* âœ… Search Box */}
            <div style={{ marginBottom: "15px" }}>
              <input
                type="text"
                placeholder="Search by name or email"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                  padding: "8px",
                  width: "300px",
                  borderRadius: "4px",
                  border: "1px solid #ccc",
                }}
              />
            </div>

            <div className="table-wrapper">
              <table className="question-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>View History</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user, index) => (
                    <tr key={user.id}>
                      <td>{index + 1}</td>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>{user.role}</td>
                      <td>
                        <button
                          className="edit-btn"
                          onClick={() => alert(`Viewing history of ${user.name}`)}
                        >
                          View History
                        </button>
                      </td>
                    </tr>
                  ))}
                  {filteredUsers.length === 0 && (
                    <tr>
                      <td colSpan="5" style={{ textAlign: "center", padding: "10px" }}>
                        No users found.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </section>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default AdminDashboard;
