import React, { useState } from "react";
import { Navbar, Footer } from "./CommonUI";

const Dashboard = ({ isDarkMode, toggleDarkMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [recordPage, setRecordPage] = useState(0);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // Dummy Data
  const leaderboard = [
    { rank: 1, name: "Alice", score: "95%" },
    { rank: 2, name: "Bob", score: "90%" },
    { rank: 3, name: "Charlie", score: "85%" },
    { rank: 4, name: "David", score: "82%" },
    { rank: 5, name: "Eve", score: "80%" },
  ];

  const pastRecords = [
    { date: "01-10-2025", score: "80%" },
    { date: "05-10-2025", score: "75%" },
    { date: "10-10-2025", score: "82%" },
    { date: "15-10-2025", score: "78%" },
    { date: "20-10-2025", score: "88%" },
    { date: "25-10-2025", score: "85%" },
    { date: "30-10-2025", score: "90%" },
    { date: "04-11-2025", score: "92%" },
    { date: "09-11-2025", score: "87%" },
    { date: "14-11-2025", score: "89%" },
  ];

  const last5Exams = [
    { day: "Day 1", percentage: 80 },
    { day: "Day 2", percentage: 75 },
    { day: "Day 3", percentage: 82 },
    { day: "Day 4", percentage: 78 },
    { day: "Day 5", percentage: 88 },
  ];

  const averageMarks = 78; // For pie chart
  const totalDaysActive = 45; // Dummy total days active
  const totalAttempts = 10;
  const position = "3rd";

  const blueShade = "#1E90FF";

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        minHeight: "100vh",
        backgroundColor: "#ffffff",
        transition: "all 0.3s",
      }}
    >
      {/* ---------- NAVBAR ---------- */}
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={true}
        navbarStyle={{
          backgroundColor: "#06400F",
          color: "white",
          boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
        }}
      />

      <div style={{ display: "flex" }}>
        {/* ---------- SIDEBAR ---------- */}
        <div
          style={{
            width: sidebarOpen ? "220px" : "40px",
            backgroundColor: "#06400F",
            color: "white",
            transition: "width 0.3s",
            padding: "20px 10px",
            display: "flex",
            flexDirection: "column",
            alignItems: sidebarOpen ? "flex-start" : "center",
            borderRight: "1px solid #0B3D02",
            minHeight: "calc(100vh - 60px)",
          }}
        >
          <button
            onClick={toggleSidebar}
            style={{
              backgroundColor: sidebarOpen ? "#0B3D02" : "#06400F",
              color: "white",
              border: "none",
              padding: "8px",
              borderRadius: "5px",
              cursor: "pointer",
              marginBottom: "20px",
              width: sidebarOpen ? "100%" : "35px",
              transition: "all 0.3s",
            }}
          >
            {sidebarOpen ? "Close" : "≡"}
          </button>

          {sidebarOpen && (
            <div style={{ marginTop: "20px", fontSize: "14px" }}>
              <div style={{ marginBottom: "15px", fontWeight: "bold" }}>Profile</div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Name:</strong> John Doe
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Email:</strong> john.doe@example.com
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Role:</strong> Student
              </div>
            </div>
          )}
        </div>

        {/* ---------- MAIN CONTENT ---------- */}
        <div style={{ flex: 1, padding: "30px", color: "#06400F" }}>
          {/* Dashboard title + start button */}
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
            <h2>Dashboard</h2>
            <button
              style={{
                backgroundColor: blueShade,
                color: "white",
                border: "none",
                padding: "10px 20px",
                borderRadius: "8px",
                cursor: "pointer",
                fontWeight: "bold",
                boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
                transition: "all 0.3s",
              }}
              onMouseEnter={(e) => (e.target.style.backgroundColor = "#1C86EE")}
              onMouseLeave={(e) => (e.target.style.backgroundColor = blueShade)}
            >
              Start Exam
            </button>
          </div>

          {/* ---------- Summary Cards ---------- */}
          <div style={{ display: "flex", gap: "20px", marginTop: "20px" }}>
            {[
              { title: "Total Attempts", value: totalAttempts },
              { title: "Total Days Active", value: totalDaysActive },
              { title: "Position", value: position },
            ].map((card, idx) => (
              <div
                key={idx}
                style={{
                  flex: 1,
                  backgroundColor: blueShade,
                  padding: "20px",
                  borderRadius: "12px",
                  boxShadow: "0 4px 10px rgba(0,0,0,0.2)",
                  textAlign: "center",
                  color: "white",
                  cursor: "pointer",
                  transition: "all 0.3s",
                }}
                onMouseEnter={(e) => {
                  e.currentTarget.style.transform = "translateY(-5px)";
                  e.currentTarget.style.boxShadow = "0 8px 15px rgba(0,0,0,0.3)";
                }}
                onMouseLeave={(e) => {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "0 4px 10px rgba(0,0,0,0.2)";
                }}
              >
                <div style={{ fontSize: "18px", fontWeight: "bold" }}>{card.title}</div>
                <div style={{ fontSize: "28px", marginTop: "10px" }}>{card.value}</div>
              </div>
            ))}
          </div>

{/* ---------- Pie Chart + Description ---------- */}
<div
  style={{
    display: "flex",
    gap: "30px",
    marginTop: "40px",
    padding: "20px",
    border: "1px solid #0B3D02",
    borderRadius: "12px",
    backgroundColor: "#f0f8ff",
    alignItems: "center",
  }}
>
  {/* Pie chart with hover animation */}
  <div
    style={{
      flex: 1,
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    }}
  >
    <div
      style={{
        width: "200px",
        height: "200px",
        borderRadius: "50%",
        background: `conic-gradient(${blueShade} 0% ${averageMarks}%, #ddd ${averageMarks}% 100%)`,
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        fontSize: "24px",
        fontWeight: "bold",
        color: "#06400F",
        cursor: "pointer",
      }}
      onMouseEnter={(e) => {
        let progress = 0;
        const interval = setInterval(() => {
          progress++;
          if (progress > averageMarks) clearInterval(interval);
          e.currentTarget.style.background = `conic-gradient(${blueShade} 0% ${progress}%, #ddd ${progress}% 100%)`;
        }, 30); // 30ms interval → ~3s animation for 100%
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.background = `conic-gradient(${blueShade} 0% ${averageMarks}%, #ddd ${averageMarks}% 100%)`;
      }}
    >
      {averageMarks}%
    </div>
  </div>

  {/* Description */}
  <div style={{ flex: 2, fontSize: "16px", color: "#06400F" }}>
    This pie chart represents your <strong>average marks</strong> across all attempted exams.
    It gives a quick visual understanding of your overall performance and progress over time.
  </div>
</div>




          {/* ---------- Graph for Last 5 Exams ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "12px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3 style={{ marginBottom: "20px" }}>Last 5 Exams Performance</h3>
            <div style={{ display: "flex", gap: "10px" }}>
              {/* Y-axis labels */}
              <div
                style={{
                  display: "flex",
                  flexDirection: "column",
                  justifyContent: "space-between",
                  height: "160px",
                  fontSize: "12px",
                  color: "#06400F",
                }}
              >
                {[100, 75, 50, 25, 0].map((val, idx) => (
                  <span key={idx}>{val}%</span>
                ))}
              </div>

              {/* Bars */}
              <div
                style={{
                  display: "flex",
                  flex: 1,
                  justifyContent: "space-between",
                  alignItems: "flex-end",
                  height: "160px",
                }}
              >
                {last5Exams.map((exam, idx) => (
                  <div key={idx} style={{ textAlign: "center", flex: 1, margin: "0 3px" }}>
                    <div
                      style={{
                        height: `${exam.percentage * 1.5}px`,
                        width: "25px",
                        backgroundColor: blueShade,
                        borderRadius: "6px 6px 0 0",
                        position: "relative",
                      }}
                    >
                      <span
                        style={{
                          position: "absolute",
                          top: "-20px",
                          left: "50%",
                          transform: "translateX(-50%)",
                          fontSize: "12px",
                          fontWeight: "bold",
                          color: "#06400F",
                        }}
                      >
                        {exam.percentage}%
                      </span>
                    </div>
                    <div style={{ marginTop: "10px", fontSize: "12px" }}>{exam.day}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* ---------- Leaderboard ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "12px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3>Leaderboard (Top 5)</h3>
            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}>
              <thead>
                <tr>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>Rank</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>Name</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((item) => (
                  <tr key={item.rank}>
                    <td style={{ textAlign: "center", padding: "8px" }}>{item.rank}</td>
                    <td style={{ textAlign: "center", padding: "8px" }}>{item.name}</td>
                    <td style={{ textAlign: "center", padding: "8px" }}>{item.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* ---------- Past Exam Records ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "12px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3>Past Exam Records</h3>
            <table style={{ width: "100%", borderCollapse: "collapse", marginTop: "10px" }}>
              <thead>
                <tr>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>S.No</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>Date</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "8px" }}>Score</th>
                </tr>
              </thead>
              <tbody>
                {pastRecords.slice(recordPage * 5, recordPage * 5 + 5).map((record, idx) => (
                  <tr key={idx}>
                    <td style={{ textAlign: "center", padding: "8px" }}>{recordPage * 5 + idx + 1}</td>
                    <td style={{ textAlign: "center", padding: "8px" }}>{record.date}</td>
                    <td style={{ textAlign: "center", padding: "8px" }}>{record.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{ marginTop: "10px", display: "flex", justifyContent: "space-between" }}>
              <button
                onClick={() => setRecordPage((prev) => Math.max(prev - 1, 0))}
                disabled={recordPage === 0}
                style={{
                  padding: "6px 12px",
                  borderRadius: "6px",
                  border: "none",
                  backgroundColor: blueShade,
                  color: "white",
                  cursor: "pointer",
                }}
              >
                Back
              </button>
              <button
                onClick={() => setRecordPage((prev) => (prev + 1) * 5 < pastRecords.length ? prev + 1 : prev)}
                disabled={(recordPage + 1) * 5 >= pastRecords.length}
                style={{
                  padding: "6px 12px",
                  borderRadius: "6px",
                  border: "none",
                  backgroundColor: blueShade,
                  color: "white",
                  cursor: "pointer",
                }}
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* ---------- FOOTER ---------- */}
      <Footer />
    </div>
  );
};

export default Dashboard;
