import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";
import Home from "./pages/Home";
import Login from "./pages/Login";
import Signup from "./pages/Signup";
import Dashboard from "./pages/Dashboard";
import AdminDashboard from "./pages/AdminDashboard";
import Exam from "./pages/Exam";
import Result from "./pages/Result";

function App() {
  // ðŸŸ£ Keep dark mode here globally
  const [isDarkMode, setIsDarkMode] = useState(false);

  // Toggle dark mode
  const toggleDarkMode = () => setIsDarkMode((prev) => !prev);

  // Define theme colors (optional)
  const appStyle = {
    backgroundColor: isDarkMode ? "#121212" : "#ffffff",
    color: isDarkMode ? "#f0f0f0" : "#000000",
    minHeight: "100vh",
    transition: "all 0.3s",
  };

  return (
    <div style={appStyle}>
      <Router>
        <Routes>
          {/* Default route */}
          <Route path="/" element={<Home isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />} />

          {/* Auth routes */}
          <Route path="/login" element={<Login isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />} />
          <Route path="/signup" element={<Signup isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />} />

          {/* User routes */}
          <Route
            path="/dashboard"
            element={<Dashboard isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />}
          />
          <Route
            path="/admin-dashboard"
            element={<AdminDashboard isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />}
          />
          <Route path="/exam" element={<Exam isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />} />
          <Route path="/result" element={<Result isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} />} />

          {/* If no route matches, redirect to Home */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
