import React, { useState, useEffect } from "react";

const Home = () => {
  const syllabus = [
    {
      title: "Reasoning",
      topics: ["Blood Relation", "Coding-Decoding", "Series", "Puzzles"],
    },
    {
      title: "Quantitative",
      topics: ["Percentages", "Profit & Loss", "Time & Work", "Averages"],
    },
    {
      title: "Verbal",
      topics: ["Synonyms", "Antonyms", "Reading Comprehension", "Grammar"],
    },
  ];

  const stats = [
    { label: "Questions", value: "500+" },
    { label: "Practice Sets", value: "50+" },
    { label: "Students Benefited", value: "1000+" },
  ];

  const faqs = [
    { q: "How can I start practicing?", a: "Click on Login or Signup to access practice sets." },
    { q: "Are tests timed?", a: "Yes, each test has a fixed duration to simulate real exams." },
    { q: "Is my progress saved?", a: "Yes, your progress and scores are saved in your account." },
  ];

  const [isDarkMode, setIsDarkMode] = useState(false);
  const [isSticky, setIsSticky] = useState(false);

  // Sticky Navbar Effect
  useEffect(() => {
    const handleScroll = () => {
      setIsSticky(window.scrollY > 50);
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif", backgroundColor: isDarkMode ? "#121212" : "#fff", color: isDarkMode ? "#fff" : "#222" }}>

      {/* ---------- INLINE CSS ---------- */}
      <style>
        {`
          .card {
            background-color: #145A32;
            width: 220px;
            padding: 30px;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0,0,0,0.3);
            color: white;
            font-weight: bold;
            font-size: 20px;
            transition: all 0.3s ease;
            cursor: pointer;
            position: relative;
            overflow: hidden;
          }

          .card:hover {
            transform: translateY(-5px);
            background: linear-gradient(135deg, #1E8449, #27AE60);
          }

          .topics {
            margin-top: 10px;
            opacity: 0;
            max-height: 0;
            transition: all 0.4s ease;
            overflow: hidden;
            font-size: 15px;
            text-align: left;
            color: #e8f5e9;
          }

          .card:hover .topics {
            opacity: 1;
            max-height: 200px;
            padding-top: 10px;
          }

          .topic-item {
            margin: 5px 0;
            padding-left: 10px;
          }

          .topic-item::before {
            content: "• ";
            color: #c8e6c9;
          }

          .faq-item {
            margin-bottom: 15px;
            text-align: left;
            cursor: pointer;
            transition: all 0.3s;
          }

          .faq-answer {
            margin-top: 5px;
            display: none;
          }

          .faq-item.open .faq-answer {
            display: block;
          }
        `}
      </style>

      {/* ---------- NAVBAR ---------- */}
      <nav
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          backgroundColor: isSticky ? "#07400F" : "#0B3D02",
          padding: "15px 50px",
          color: "white",
          position: "sticky",
          top: 0,
          zIndex: 1000,
          boxShadow: isSticky ? "0 4px 8px rgba(0,0,0,0.4)" : "0 2px 5px rgba(0,0,0,0.3)",
          transition: "all 0.3s ease",
        }}
      >
        <h2 style={{ margin: 0 }}>Aptitude Preparation</h2>
        <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
          <button
            style={{
              backgroundColor: "#FFD700",
              color: "#0B3D02",
              border: "none",
              padding: "8px 18px",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Free Trial
          </button>
          <button
            style={{
              backgroundColor: "white",
              color: "#0B3D02",
              border: "none",
              padding: "8px 18px",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Login
          </button>
          <button
            onClick={toggleDarkMode}
            style={{
              marginLeft: "15px",
              padding: "5px 10px",
              borderRadius: "5px",
              border: "none",
              cursor: "pointer",
            }}
          >
            {isDarkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>
      </nav>

      {/* ---------- INTRO SECTION ---------- */}
      <section
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "60px 100px",
          backgroundColor: isDarkMode ? "#1A1A1A" : "#F4F7F4",
        }}
      >
        <img
          src="/home.jpg"
          alt="Aptitude Preparation"
          style={{
            width: "350px",
            borderRadius: "10px",
            marginRight: "30px",
            marginLeft: "10px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
          }}
        />
        <p
          style={{
            fontSize: "22px",
            lineHeight: "1.8",
            maxWidth: "550px",
            textAlign: "justify",
            fontFamily: "'Segoe UI', sans-serif",
          }}
        >
          Welcome to the <b>Aptitude Preparation</b> platform! Here, you can
          sharpen your reasoning, quantitative, and verbal skills through
          well-structured practice sets. Prepare confidently for your placement
          exams and boost your problem-solving potential with focused learning.
        </p>
      </section>

      {/* ---------- STATISTICS / PROGRESS SECTION ---------- */}
      <section
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "60px",
          padding: "50px",
          textAlign: "center",
        }}
      >
        {stats.map((s, index) => (
          <div key={index} style={{ fontSize: "20px", fontWeight: "bold" }}>
            <div style={{ fontSize: "32px", color: "#0B3D02" }}>{s.value}</div>
            <div>{s.label}</div>
          </div>
        ))}
      </section>

      {/* ---------- SYLLABUS SECTION ---------- */}
      <section
        style={{
          padding: "50px 100px",
          textAlign: "center",
          backgroundColor: "#0B3D02",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "40px" }}>Syllabus</h2>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "40px",
            flexWrap: "wrap",
          }}
        >
          {syllabus.map((item, index) => (
            <div key={index} className="card">
              {item.title}
              <div className="topics">
                {item.topics.map((topic, i) => (
                  <div key={i} className="topic-item">
                    {topic}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* ---------- FAQ SECTION ---------- */}
      <section style={{ padding: "50px 100px", textAlign: "center" }}>
        <h2 style={{ color: "#0B3D02", marginBottom: "40px" }}>FAQ</h2>
        {faqs.map((f, i) => (
          <FAQItem key={i} question={f.q} answer={f.a} />
        ))}
      </section>

      {/* ---------- FOOTER ---------- */}
      <footer
        style={{
          backgroundColor: "#0B3D02",
          color: "white",
          textAlign: "center",
          padding: "20px 10px",
          fontSize: "16px",
          boxShadow: "0 -2px 5px rgba(0,0,0,0.3)",
          marginTop: "50px",
        }}
      >
        © {new Date().getFullYear()} Aptitude Preparation | Designed with ❤️ by
        Shamal
      </footer>
    </div>
  );
};

// FAQ Item Component
const FAQItem = ({ question, answer }) => {
  const [open, setOpen] = useState(false);
  return (
    <div
      className={`faq-item ${open ? "open" : ""}`}
      onClick={() => setOpen(!open)}
      style={{
        border: "1px solid #0B3D02",
        borderRadius: "8px",
        padding: "15px",
        maxWidth: "600px",
        margin: "0 auto 15px",
        backgroundColor: "#f9f9f9",
      }}
    >
      <div style={{ fontWeight: "bold", color: "#0B3D02" }}>{question}</div>
      <div className="faq-answer" style={{ marginTop: "10px", color: "#222" }}>
        {answer}
      </div>
    </div>
  );
};

export default Home;
