import React, { useState } from "react";
import { Navbar, Footer } from "./CommonUI";

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    "Welcome to Admin Dashboard",
    "Manage Users",
    "View Reports",
    "Settings",
  ];

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const nextSlide = () => setCurrentSlide((currentSlide + 1) % slides.length);
  const prevSlide = () =>
    setCurrentSlide((currentSlide - 1 + slides.length) % slides.length);

  return (
    <div style={{ position: "relative", minHeight: "100vh" }}>
      {/* Sidebar */}
      <div
        style={{
          position: "fixed",
          top: 0,
          left: 0,
          width: sidebarOpen ? "220px" : "60px",
          height: "100%",
          backgroundColor: "#4b0082",
          color: "white",
          padding: "15px",
          boxSizing: "border-box",
          transition: "width 0.3s",
          zIndex: 1000,
        }}
      >
        <button
          onClick={toggleSidebar}
          style={{
            backgroundColor: "#6a0dad",
            border: "none",
            color: "white",
            padding: "10px",
            width: "100%",
            cursor: "pointer",
            marginBottom: "20px",
          }}
        >
          {sidebarOpen ? "Close" : "≡"}
        </button>

        {sidebarOpen && (
          <div>
            <h4>Profile</h4>
            <p><strong>Name:</strong> Admin Name</p>
            <p><strong>Email:</strong> admin@example.com</p>
            <p><strong>Role:</strong> Admin</p>
          </div>
        )}
      </div>

      {/* Main content wrapper */}
      <div style={{ marginLeft: sidebarOpen ? "220px" : "60px", transition: "margin-left 0.3s" }}>
        {/* Navbar */}
        <Navbar />

        {/* Slider */}
        <div
          style={{
            backgroundColor: "#ffffff", // white slider
            padding: "40px",
            margin: "20px",
            borderRadius: "10px",
            textAlign: "center",
            fontSize: "1.2rem",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
          }}
        >
          <button
            onClick={prevSlide}
            style={{
              backgroundColor: "#6a0dad",
              color: "white",
              border: "none",
              padding: "10px 15px",
              marginRight: "15px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            ◀
          </button>

          <div style={{ minWidth: "300px" }}>{slides[currentSlide]}</div>

          <button
            onClick={nextSlide}
            style={{
              backgroundColor: "#6a0dad",
              color: "white",
              border: "none",
              padding: "10px 15px",
              marginLeft: "15px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            ▶
          </button>
        </div>

        {/* Footer */}
        <Footer />
      </div>
    </div>
  );
};

export default AdminDashboard;
