import React, { useState, useEffect } from "react";

const Home = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [counters, setCounters] = useState([0, 0, 0]); // animated stats counters

  const syllabus = [
    {
      title: "Reasoning",
      topics: ["Blood Relation", "Coding-Decoding", "Series", "Puzzles"],
    },
    {
      title: "Quantitative",
      topics: ["Percentages", "Profit & Loss", "Time & Work", "Averages"],
    },
    {
      title: "Verbal",
      topics: ["Synonyms", "Antonyms", "Reading Comprehension", "Grammar"],
    },
  ];

  const stats = [
    { label: "Questions", value: 500 },
    { label: "Practice Sets", value: 50 },
    { label: "Students Benefited", value: 1000 },
  ];

  // Scroll detection for sticky navbar
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 50) setScrolled(true);
      else setScrolled(false);

      // Scroll animations: fade-up
      const fadeSections = document.querySelectorAll(".fade-up");
      fadeSections.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.style.opacity = 1;
          el.style.transform = "translateY(0)";
        }
      });
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Animate counters
  useEffect(() => {
    let start = [0, 0, 0];
    const end = stats.map((s) => s.value);
    const duration = 2000; // 2 seconds
    const stepTime = 30;
    let interval = setInterval(() => {
      let done = true;
      start = start.map((val, i) => {
        if (val < end[i]) {
          done = false;
          return Math.min(val + Math.ceil(end[i] / (duration / stepTime)), end[i]);
        } else return val;
      });
      setCounters([...start]);
      if (done) clearInterval(interval);
    }, stepTime);
  }, []);

  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        backgroundColor: isDarkMode ? "#121212" : "#fff",
        color: isDarkMode ? "#f1f1f1" : "#222",
        transition: "all 0.3s",
      }}
    >
      {/* ---------- INLINE CSS ---------- */}
      <style>
        {`
          .card {
            background-color: #145A32;
            width: 220px;
            padding: 30px;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0,0,0,0.3);
            color: white;
            font-weight: bold;
            font-size: 20px;
            transition: all 0.3s ease;
            cursor: pointer;
            position: relative;
            overflow: hidden;
          }

          .topics {
            margin-top: 10px;
            opacity: 0;
            max-height: 0;
            transition: all 0.5s ease;
            overflow: hidden;
            font-size: 15px;
            text-align: left;
            color: #e8f5e9;
          }

          .card:hover {
            transform: translateY(-5px);
            background-color: #1E8449;
          }

          .card:hover .topics {
            opacity: 1;
            max-height: 200px;
            padding-top: 10px;
          }

          .topic-item {
            margin: 5px 0;
            padding-left: 10px;
            display: flex;
            align-items: center;
            opacity: 0;
            transform: translateX(-20px);
            transition: all 0.4s ease;
          }

          .card:hover .topic-item {
            opacity: 1;
            transform: translateX(0);
          }

          .topic-item::before {
            content: "• ";
            color: #c8e6c9;
            margin-right: 5px;
          }

          .fade-up {
            opacity: 0;
            transform: translateY(30px);
            transition: all 0.8s ease;
          }

          .hero-img {
            transition: transform 0.5s ease;
          }

          .hero-img:hover {
            transform: scale(1.05);
          }

          .social-icons img {
            width: 40px;
            margin: 0 10px;
            transition: transform 0.3s, filter 0.3s;
            cursor: pointer;
          }

          .social-icons img:hover {
            transform: scale(1.2);
            filter: brightness(1.2);
          }
        `}
      </style>

      {/* ---------- NAVBAR ---------- */}
      <nav
        style={{
          position: "sticky",
          top: 0,
          zIndex: 100,
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          backgroundColor: scrolled ? "#09400B" : "#0B3D02",
          padding: "15px 50px",
          color: "white",
          boxShadow: scrolled
            ? "0 4px 8px rgba(0,0,0,0.4)"
            : "0 2px 5px rgba(0,0,0,0.3)",
          transition: "all 0.3s",
        }}
      >
        <h2 style={{ margin: 0 }}>Aptitude Preparation</h2>
        <div style={{ display: "flex", gap: "10px" }}>
          <button
            style={{
              backgroundColor: "#FFD700",
              color: "#0B3D02",
              border: "none",
              padding: "8px 18px",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Free Trial
          </button>
          <button
            style={{
              backgroundColor: "white",
              color: "#0B3D02",
              border: "none",
              padding: "8px 18px",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
            }}
          >
            Login
          </button>
          <button
            onClick={toggleDarkMode}
            style={{
              marginLeft: "10px",
              border: "none",
              padding: "8px 15px",
              borderRadius: "5px",
              cursor: "pointer",
              fontWeight: "bold",
              backgroundColor: isDarkMode ? "#FFA500" : "#222",
              color: isDarkMode ? "#222" : "#fff",
            }}
          >
            {isDarkMode ? "Light Mode" : "Dark Mode"}
          </button>
        </div>
      </nav>

      {/* ---------- INTRO SECTION ---------- */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "60px 100px",
          backgroundColor: isDarkMode ? "#1E1E1E" : "#F4F7F4",
          borderRadius: "10px",
          margin: "20px 50px",
        }}
      >
        <img
          src="/home.jpg"
          alt="Aptitude Preparation"
          className="hero-img"
          style={{
            width: "350px",
            borderRadius: "10px",
            marginRight: "20px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
          }}
        />

        <p
          style={{
            fontSize: "22px",
            lineHeight: "1.8",
            maxWidth: "550px",
            textAlign: "justify",
            fontFamily: "'Segoe UI', sans-serif",
          }}
        >
          Welcome to the <b>Aptitude Preparation</b> platform! Here, you can
          sharpen your reasoning, quantitative, and verbal skills through
          well-structured practice sets. Prepare confidently for your placement
          exams and boost your problem-solving potential with focused learning.
        </p>
      </section>

      {/* ---------- SYLLABUS SECTION ---------- */}
      <section
        className="fade-up"
        style={{
          padding: "50px 100px",
          textAlign: "center",
          backgroundColor: "#0B3D02",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "40px" }}>Syllabus</h2>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "40px",
            flexWrap: "wrap",
          }}
        >
          {syllabus.map((item, index) => (
            <div key={index} className="card">
              {item.title}
              <div className="topics">
                {item.topics.map((topic, i) => (
                  <div key={i} className="topic-item">
                    {topic}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* ---------- STATISTICS / PROGRESS SECTION ---------- */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "60px",
          padding: "50px",
          textAlign: "center",
          backgroundColor: isDarkMode ? "#222" : "#F4F7F4",
        }}
      >
        {stats.map((s, index) => (
          <div key={index} style={{ fontSize: "20px", fontWeight: "bold" }}>
            <div style={{ fontSize: "32px", color: "#0B3D02" }}>
              {counters[index]}+
            </div>
            <div>{s.label}</div>
          </div>
        ))}
      </section>

      {/* ---------- SOCIAL MEDIA SECTION ---------- */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "30px",
          padding: "30px 0",
          backgroundColor: isDarkMode ? "#111" : "#F4F7F4",
        }}
      >
        <div className="social-icons">
          <img src="/instagram.png" alt="Instagram" />
          {/* <img src="/linkedin.png" alt="LinkedIn" /> */}
          <img src="/youtube.png" alt="YouTube" />
        </div>
      </section>

      {/* ---------- FOOTER ---------- */}
      <footer
        style={{
          backgroundColor: "#0B3D02",
          color: "white",
          textAlign: "center",
          padding: "20px 10px",
          fontSize: "16px",
          boxShadow: "0 -2px 5px rgba(0,0,0,0.3)",
        }}
      >
        © {new Date().getFullYear()} Aptitude Preparation | Designed with ❤️ by
        Shamal
      </footer>
    </div>
  );
};

export default Home;
