import React from "react";

const Home = () => {
  const syllabus = [
    {
      title: "Reasoning",
      topics: ["Blood Relation", "Coding-Decoding", "Series", "Puzzles"],
    },
    {
      title: "Quantitative",
      topics: ["Percentages", "Profit & Loss", "Time & Work", "Averages"],
    },
    {
      title: "Verbal",
      topics: ["Synonyms", "Antonyms", "Reading Comprehension", "Grammar"],
    },
  ];

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      {/* ---------- INLINE CSS ---------- */}
      <style>
        {`
          .card {
            background-color: #145A32;
            width: 220px;
            padding: 30px;
            border-radius: 10px;
            box-shadow: 0 4px 10px rgba(0,0,0,0.3);
            color: white;
            font-weight: bold;
            font-size: 20px;
            transition: all 0.3s ease;
            cursor: pointer;
            position: relative;
            overflow: hidden;
          }

          .topics {
            margin-top: 10px;
            opacity: 0;
            max-height: 0;
            transition: all 0.4s ease;
            overflow: hidden;
            font-size: 15px;
            text-align: left;
            color: #e8f5e9;
          }

          .card:hover {
            transform: translateY(-5px);
            background-color: #1E8449;
          }

          .card:hover .topics {
            opacity: 1;
            max-height: 200px;
            padding-top: 10px;
          }

          .topic-item {
            margin: 5px 0;
            padding-left: 10px;
          }

          .topic-item::before {
            content: "• ";
            color: #c8e6c9;
          }
        `}
      </style>

      {/* ---------- NAVBAR ---------- */}
      <nav
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          backgroundColor: "#0B3D02",
          padding: "15px 50px",
          color: "white",
          boxShadow: "0 2px 5px rgba(0,0,0,0.3)",
        }}
      >
        <h2 style={{ margin: 0 }}>Aptitude Preparation</h2>
        <button
          style={{
            backgroundColor: "white",
            color: "#0B3D02",
            border: "none",
            padding: "8px 18px",
            borderRadius: "5px",
            cursor: "pointer",
            fontWeight: "bold",
          }}
        >
          Login
        </button>
      </nav>

      {/* ---------- INTRO SECTION ---------- */}
      <section
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "60px 100px",
          backgroundColor: "#F4F7F4",
        }}
      >
        <img
            src="/home.jpg"
            alt="Aptitude Preparation"
            style={{
                width: "350px",
                borderRadius: "10px",
                marginRight: "30px", // reduced from 50px
                marginLeft: "20px", // optional to balance left spacing
                boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
            }}
            />

        <p
          style={{
            fontSize: "22px",
            lineHeight: "1.8",
            color: "#222",
            maxWidth: "550px",
            textAlign: "justify",
            fontFamily: "'Segoe UI', sans-serif",
          }}
        >
          Welcome to the <b>Aptitude Preparation</b> platform! Here, you can
          sharpen your reasoning, quantitative, and verbal skills through
          well-structured practice sets. Prepare confidently for your placement
          exams and boost your problem-solving potential with focused learning.
        </p>
      </section>

      {/* ---------- SYLLABUS SECTION ---------- */}
      <section
        style={{
          padding: "50px 100px",
          textAlign: "center",
          backgroundColor: "#0B3D02",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "40px" }}>Syllabus</h2>

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "40px",
            flexWrap: "wrap",
          }}
        >
          {syllabus.map((item, index) => (
            <div key={index} className="card">
              {item.title}
              <div className="topics">
                {item.topics.map((topic, i) => (
                  <div key={i} className="topic-item">
                    {topic}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* ---------- FOOTER ---------- */}
      <footer
        style={{
          backgroundColor: "#0B3D02",
          color: "white",
          textAlign: "center",
          padding: "20px 10px",
          fontSize: "16px",
          boxShadow: "0 -2px 5px rgba(0,0,0,0.3)",
          marginTop: "50px",
        }}
      >
        © {new Date().getFullYear()} Aptitude Preparation | Designed with ❤️ by
        Shamal
      </footer>
    </div>
  );
};

export default Home;
