import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "../css/freetest.css";

const FreeTest = () => {
  const [showResult, setShowResult] = useState(false);
  const navigate = useNavigate();

  const dummyQuestions = [
    {
      question: "What is 2 + 2?",
      options: ["1", "2", "3", "4"],
    },
    {
      question: "Which is a programming language?",
      options: ["HTML", "CSS", "Python", "Photoshop"],
    },
    {
      question: "What color is the sky?",
      options: ["Blue", "Green", "Red", "Yellow"],
    },
    {
      question: "Select the correct spelling:",
      options: ["Recieve", "Receive", "Recive", "Receve"],
    },
    {
      question: "Which planet is known as the Red Planet?",
      options: ["Earth", "Mars", "Jupiter", "Venus"],
    },
    {
      question: "What is 5 * 5?",
      options: ["10", "15", "25", "30"],
    },
    {
      question: "Which animal is known as the King of Jungle?",
      options: ["Lion", "Tiger", "Elephant", "Leopard"],
    },
    {
      question: "Which is used to style web pages?",
      options: ["HTML", "CSS", "JavaScript", "Python"],
    },
    {
      question: "What is the capital of India?",
      options: ["Delhi", "Mumbai", "Kolkata", "Chennai"],
    },
    {
      question: "Which gas do plants release?",
      options: ["Oxygen", "Carbon Dioxide", "Nitrogen", "Hydrogen"],
    },
  ];

  const handleRestart = () => {
    setShowResult(false);
    // Optionally reset selected options
    const inputs = document.querySelectorAll('input[type="radio"]');
    inputs.forEach(input => (input.checked = false));
  };

  const handleHome = () => {
    navigate("/");
  };

  return (
    <div className="freetest-container">
      <h1>Free Test Module</h1>
      {dummyQuestions.map((q, idx) => (
        <div className="question-card" key={idx}>
          <p className="question">{idx + 1}. {q.question}</p>
          <div className="options">
            {q.options.map((opt, i) => (
              <label key={i}>
                <input type="radio" name={`q${idx}`} />
                {opt}
              </label>
            ))}
          </div>
        </div>
      ))}

      {!showResult && (
        <button className="submit-btn" onClick={() => setShowResult(true)}>
          Submit
        </button>
      )}

      {showResult && (
        <div className="result">
          <h2>Your Marks: 7 / 10</h2> {/* Dummy mark */}
          <div className="result-buttons">
            <button className="restart-btn" onClick={handleRestart}>
              Restart
            </button>
            <button className="home-btn" onClick={handleHome}>
              Home
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FreeTest;
