import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./CommonUI";
import "../css/dashboard.css";
import { useNavigate } from "react-router-dom";

const Dashboard = ({ isDarkMode, toggleDarkMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [recordPage, setRecordPage] = useState(0);
  const [isEditing, setIsEditing] = useState(false);
  const [user, setUser] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate(); // Initialize navigate

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  useEffect(() => {
    // Load user data from localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const userData = JSON.parse(storedUser);
      setUser(userData);
      setEditForm({
        name: userData.name || '',
        email: userData.email || '',
        password: ''
      });
    }
  }, []);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setError('');
    // Reset form to current user data
    if (user) {
      setEditForm({
        name: user.name || '',
        email: user.email || '',
        password: ''
      });
    }
  };

  const handleUpdate = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Not authenticated');
      }

      // Only include fields that have been changed
      const updates = {};
      if (editForm.name !== user.name) updates.name = editForm.name;
      if (editForm.email !== user.email) updates.email = editForm.email;
      if (editForm.password) updates.password = editForm.password;

      const res = await fetch('http://localhost:5000/api/users/me', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updates)
      });

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.message || 'Update failed');
      }

      const data = await res.json();
      
      // Update local storage and state
      localStorage.setItem('user', JSON.stringify(data.user));
      setUser(data.user);
      setIsEditing(false);
      setError('');
    } catch (err) {
      setError(err.message || 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const leaderboard = [
    { rank: 1, name: "Alice", score: "95%" },
    { rank: 2, name: "Bob", score: "90%" },
    { rank: 3, name: "Charlie", score: "85%" },
    { rank: 4, name: "David", score: "82%" },
    { rank: 5, name: "Eve", score: "80%" },
  ];

  const pastRecords = [
    { date: "01-10-2025", score: "80%" },
    { date: "05-10-2025", score: "75%" },
    { date: "10-10-2025", score: "82%" },
    { date: "15-10-2025", score: "78%" },
    { date: "20-10-2025", score: "88%" },
    { date: "25-10-2025", score: "85%" },
    { date: "30-10-2025", score: "90%" },
    { date: "04-11-2025", score: "92%" },
    { date: "09-11-2025", score: "87%" },
    { date: "14-11-2025", score: "89%" },
  ];

  const last5Exams = [
    { day: "Day 1", percentage: 80 },
    { day: "Day 2", percentage: 75 },
    { day: "Day 3", percentage: 82 },
    { day: "Day 4", percentage: 78 },
    { day: "Day 5", percentage: 88 },
  ];

  const averageMarks = 78;
  const [averageMarksState, setAverageMarksState] = useState(averageMarks);
  const [totalDaysActiveState, setTotalDaysActiveState] = useState(null);
  const [totalAttemptsState, setTotalAttemptsState] = useState(null);
  const [positionState, setPositionState] = useState(null);

  // Fetch user stats (attempts, daysActive, position)
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) return; // not logged in
        const res = await fetch('http://localhost:5000/api/results/me/stats', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) {
          console.warn('Could not fetch stats', res.status);
          return;
        }
        const data = await res.json();
        setTotalAttemptsState(data.attempts ?? 0);
        setTotalDaysActiveState(data.daysActive ?? 0);
        if (data.position) setPositionState(data.totalUsers ? `${data.position} / ${data.totalUsers}` : `${data.position}`);
      } catch (e) {
        console.error('fetchStats error', e);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className={`dashboard-page ${isDarkMode ? "dark-mode" : ""}`}>
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={true}
      />

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? "Close" : "≡"}
          </button>

          {sidebarOpen && (
            <div className="sidebar-content">
              <h4>Profile</h4>
              {!isEditing ? (
                <>
                  <p><strong>Name:</strong> {user?.name || 'Loading...'}</p>
                  <p><strong>Email:</strong> {user?.email || 'Loading...'}</p>
                  <p><strong>Role:</strong> {user?.role || 'Student'}</p>
                  <button
                    onClick={handleEdit}
                    style={{
                      backgroundColor: '#1E8449',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      marginTop: '10px',
                      width: '100%'
                    }}
                  >
                    Edit Profile
                  </button>
                </>
              ) : (
                <div className="edit-profile-form">
                  <input
                    type="text"
                    placeholder="Name"
                    value={editForm.name}
                    onChange={(e) => setEditForm({...editForm, name: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    value={editForm.email}
                    onChange={(e) => setEditForm({...editForm, email: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  <input
                    type="password"
                    placeholder="New Password (optional)"
                    value={editForm.password}
                    onChange={(e) => setEditForm({...editForm, password: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  {error && (
                    <div style={{ color: 'red', marginBottom: '8px', fontSize: '14px' }}>
                      {error}
                    </div>
                  )}
                  <div style={{ display: 'flex', gap: '8px' }}>
                    <button
                      onClick={handleUpdate}
                      disabled={loading}
                      style={{
                        backgroundColor: '#1E8449',
                        color: 'white',
                        border: 'none',
                        padding: '8px 16px',
                        borderRadius: '4px',
                        cursor: loading ? 'not-allowed' : 'pointer',
                        flex: 1
                      }}
                    >
                      {loading ? 'Updating...' : 'Save'}
                    </button>
                    <button
                      onClick={handleCancel}
                      disabled={loading}
                      style={{
                        backgroundColor: '#dc3545',
                        color: 'white',
                        border: 'none',
                        padding: '8px 16px',
                        borderRadius: '4px',
                        cursor: loading ? 'not-allowed' : 'pointer',
                        flex: 1
                      }}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* MAIN CONTENT */}
        <div className="main-content">
          <div className="dashboard-header">
            <h2>Dashboard</h2>
            <button 
              className="start-exam-btn"
              onClick={() => navigate("/exam")} // Start Exam button
            >
              Start Exam
            </button>
          </div>

          {/* SUMMARY CARDS */}
          <div className="summary-cards">
            {[
              { title: "Total Attempts", value: totalAttemptsState != null ? totalAttemptsState : '—' },
              { title: "Total Days Active", value: totalDaysActiveState != null ? totalDaysActiveState : '—' },
              { title: "Position", value: positionState != null ? positionState : '—' },
            ].map((card, idx) => (
              <div key={idx} className="summary-card">
                <div className="card-title">{card.title}</div>
                <div className="card-value">{card.value}</div>
              </div>
            ))}
          </div>

          {/* PAST EXAM RECORDS */}
          <div className="past-records">
            <h3>Past Exam Records</h3>
            <table>
              <thead>
                <tr>
                  <th>S.No</th>
                  <th>Date</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {pastRecords.slice(recordPage * 5, recordPage * 5 + 5).map((record, idx) => (
                  <tr key={idx}>
                    <td>{recordPage * 5 + idx + 1}</td>
                    <td>{record.date}</td>
                    <td>{record.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="record-buttons">
              <button
                onClick={() => setRecordPage((prev) => Math.max(prev - 1, 0))}
                disabled={recordPage === 0}
              >
                Back
              </button>
              <button
                onClick={() =>
                  setRecordPage((prev) =>
                    (prev + 1) * 5 < pastRecords.length ? prev + 1 : prev
                  )
                }
                disabled={(recordPage + 1) * 5 >= pastRecords.length}
              >
                Next
              </button>
            </div>
          </div>

          {/* PIE CHART */}
          <div className="average-pie-chart">
            <div
              className="pie-circle"
              style={{
                background: `conic-gradient(#800080 0% ${averageMarks}%, #000000ff ${averageMarks}% 100%)`,
                color: "#efd6ffff", // Light purple for text inside pie
              }}
            >
              {averageMarks}%
            </div>
            <div className="pie-description">
              This pie chart represents your <strong>average marks</strong> across all attempted exams.
            </div>
          </div>

          {/* LAST 5 EXAMS PERFORMANCE */}
          <div className="last-5-exams">
            <h3>Last 5 Exams Performance</h3>
            <div className="bar-chart">
              <div className="y-axis">
                {[100, 75, 50, 25, 0].map((val, idx) => (
                  <span key={idx}>{val}%</span>
                ))}
              </div>
              <div className="bars">
                {last5Exams.map((exam, idx) => (
                  <div key={idx} className="bar-wrapper">
                    <div
                      className="bar"
                      style={{ height: `${exam.percentage * 1.5}px` }}
                    >
                      <span className="bar-value">{exam.percentage}%</span>
                    </div>
                    <div className="bar-label">{exam.day}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* LEADERBOARD */}
          <div className="leaderboard">
            <h3>Leaderboard (Top 5)</h3>
            <table>
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Name</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((item) => (
                  <tr key={item.rank}>
                    <td>{item.rank}</td>
                    <td>{item.name}</td>
                    <td>{item.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Dashboard;
