import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./CommonUI";
import "../css/dashboard.css";
import { useNavigate } from "react-router-dom";

const Dashboard = ({ isDarkMode, toggleDarkMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [recordPage, setRecordPage] = useState(0);
  const [isEditing, setIsEditing] = useState(false);
  const [user, setUser] = useState(null);
  const [editForm, setEditForm] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate(); // Initialize navigate

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  useEffect(() => {
    // Load user data from localStorage
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      const userData = JSON.parse(storedUser);
      setUser(userData);
      setEditForm({
        name: userData.name || '',
        email: userData.email || '',
        password: ''
      });
    }
  }, []);

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleCancel = () => {
    setIsEditing(false);
    setError('');
    // Reset form to current user data
    if (user) {
      setEditForm({
        name: user.name || '',
        email: user.email || '',
        password: ''
      });
    }
  };

  const handleUpdate = async () => {
    setLoading(true);
    setError('');
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Not authenticated');
      }

      // Only include fields that have been changed
      const updates = {};
      if (editForm.name !== user.name) updates.name = editForm.name;
      if (editForm.email !== user.email) updates.email = editForm.email;
      if (editForm.password) updates.password = editForm.password;

      const res = await fetch('http://localhost:5000/api/users/me', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(updates)
      });

      if (!res.ok) {
        const data = await res.json();
        throw new Error(data.message || 'Update failed');
      }

      const data = await res.json();
      
      // Update local storage and state
      localStorage.setItem('user', JSON.stringify(data.user));
      setUser(data.user);
      setIsEditing(false);
      setError('');
    } catch (err) {
      setError(err.message || 'Failed to update profile');
    } finally {
      setLoading(false);
    }
  };

  const leaderboard = [
    { rank: 1, name: "Alice", score: "95%" },
    { rank: 2, name: "Bob", score: "90%" },
    { rank: 3, name: "Charlie", score: "85%" },
    { rank: 4, name: "David", score: "82%" },
    { rank: 5, name: "Eve", score: "80%" },
  ];

  const [pastRecords, setPastRecords] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const [fetchingRecords, setFetchingRecords] = useState(false);

  // Fetch exam history
  useEffect(() => {
    const fetchExamHistory = async () => {
      setFetchingRecords(true);
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          console.warn('No token - skip fetching exam history');
          return;
        }

        console.log('Fetching exam history for page:', recordPage);
        const apiUrl = `${process.env.REACT_APP_API_URL || 'http://localhost:5000'}/api/results/me/history?page=${recordPage}&limit=5`;
        console.log('API URL:', apiUrl);

        const res = await fetch(apiUrl, {
          headers: { 
            Authorization: `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });

        if (!res.ok) {
          console.warn('Could not fetch exam history:', res.status);
          const errorText = await res.text();
          console.warn('Error response:', errorText);
          return;
        }

        const data = await res.json();
        console.log('Received data:', data);

        if (data && Array.isArray(data.results)) {
          const formattedRecords = data.results.map(r => ({
            date: r.date,
            score: typeof r.score === 'number' ? `${r.score}%` : '0%'
          }));
          console.log('Formatted records:', formattedRecords);
          setPastRecords(formattedRecords);
          setTotalRecords(data.total || 0);
        } else {
          console.warn('Unexpected data format:', data);
          setPastRecords([]);
          setTotalRecords(0);
        }
      } catch (err) {
        console.error('Error fetching exam history:', err);
        setPastRecords([]);
        setTotalRecords(0);
      } finally {
        setFetchingRecords(false);
      }
    };

    fetchExamHistory();
  }, [recordPage]);

  const last5Exams = [
    { day: "Day 1", percentage: 80 },
    { day: "Day 2", percentage: 75 },
    { day: "Day 3", percentage: 82 },
    { day: "Day 4", percentage: 78 },
    { day: "Day 5", percentage: 88 },
  ];

  const [averageMarksState, setAverageMarksState] = useState(0);
  const [totalDaysActiveState, setTotalDaysActiveState] = useState(null);
  const [totalAttemptsState, setTotalAttemptsState] = useState(null);
  const [positionState, setPositionState] = useState(null);

  // Fetch user stats (attempts, daysActive, position)
  useEffect(() => {
    const fetchStats = async () => {
      try {
        const token = localStorage.getItem('token');
        if (!token) {
          console.warn('No token - skip fetching stats');
          return;
        }

        // Verify token by calling /api/auth/me first (ensures auth middleware will accept it)
        const meRes = await fetch('http://localhost:5000/api/auth/me', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!meRes.ok) {
          console.warn('/api/auth/me failed', meRes.status);
          return;
        }

        const res = await fetch('http://localhost:5000/api/results/me/stats', {
          headers: { Authorization: `Bearer ${token}` },
        });
        if (!res.ok) {
          const body = await res.text().catch(() => '');
          console.warn('Could not fetch stats', res.status, body);
          return;
        }
        const data = await res.json();
        console.log('Fetched stats:', data);
        setTotalAttemptsState(data.attempts ?? 0);
        setTotalDaysActiveState(data.daysActive ?? 0);
        if (data.position) setPositionState(data.totalUsers ? `${data.position} / ${data.totalUsers}` : `${data.position}`);
      } catch (e) {
        console.error('fetchStats error', e);
      }
    };
    fetchStats();
  }, []);

  return (
    <div className={`dashboard-page ${isDarkMode ? "dark-mode" : ""}`}>
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={true}
      />

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? "Close" : "≡"}
          </button>

          {sidebarOpen && (
            <div className="sidebar-content">
              <h4>Profile</h4>
              {!isEditing ? (
                <>
                  <p><strong>Name:</strong> {user?.name || 'Loading...'}</p>
                  <p><strong>Email:</strong> {user?.email || 'Loading...'}</p>
                  <p><strong>Role:</strong> {user?.role || 'Student'}</p>
                  <button
                    onClick={handleEdit}
                    style={{
                      backgroundColor: '#1E8449',
                      color: 'white',
                      border: 'none',
                      padding: '8px 16px',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      marginTop: '10px',
                      width: '100%'
                    }}
                  >
                    Edit Profile
                  </button>
                </>
              ) : (
                <div className="edit-profile-form">
                  <input
                    type="text"
                    placeholder="Name"
                    value={editForm.name}
                    onChange={(e) => setEditForm({...editForm, name: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  <input
                    type="email"
                    placeholder="Email"
                    value={editForm.email}
                    onChange={(e) => setEditForm({...editForm, email: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  <input
                    type="password"
                    placeholder="New Password (optional)"
                    value={editForm.password}
                    onChange={(e) => setEditForm({...editForm, password: e.target.value})}
                    style={{
                      width: '100%',
                      padding: '8px',
                      marginBottom: '8px',
                      borderRadius: '4px',
                      border: '1px solid #ccc'
                    }}
                  />
                  {error && (
                    <div style={{ color: 'red', marginBottom: '8px', fontSize: '14px' }}>
                      {error}
                    </div>
                  )}
                  <div style={{ display: 'flex', gap: '8px' }}>
                    <button
                      onClick={handleUpdate}
                      disabled={loading}
                      style={{
                        backgroundColor: '#1E8449',
                        color: 'white',
                        border: 'none',
                        padding: '8px 16px',
                        borderRadius: '4px',
                        cursor: loading ? 'not-allowed' : 'pointer',
                        flex: 1
                      }}
                    >
                      {loading ? 'Updating...' : 'Save'}
                    </button>
                    <button
                      onClick={handleCancel}
                      disabled={loading}
                      style={{
                        backgroundColor: '#dc3545',
                        color: 'white',
                        border: 'none',
                        padding: '8px 16px',
                        borderRadius: '4px',
                        cursor: loading ? 'not-allowed' : 'pointer',
                        flex: 1
                      }}
                    >
                      Cancel
                    </button>
                  </div>
                </div>
              )}
            </div>
          )}
        </div>

        {/* MAIN CONTENT */}
        <div className="main-content">
          <div className="dashboard-header">
            <h2>Dashboard</h2>
            <button 
              className="start-exam-btn"
              onClick={() => navigate("/exam")} // Start Exam button
            >
              Start Exam
            </button>
          </div>

          {/* SUMMARY CARDS */}
          <div className="summary-cards">
            {[
              { title: "Total Attempts", value: totalAttemptsState != null ? totalAttemptsState : '—' },
              { title: "Total Days Active", value: totalDaysActiveState != null ? totalDaysActiveState : '—' },
              { title: "Position", value: positionState != null ? positionState : '—' },
            ].map((card, idx) => (
              <div key={idx} className="summary-card">
                <div className="card-title">{card.title}</div>
                <div className="card-value">{card.value}</div>
              </div>
            ))}
          </div>

          {/* PAST EXAM RECORDS */}
          <div className="past-records">
            <h3>Past Exam Records</h3>
            <table>
              <thead>
                <tr>
                  <th>S.No</th>
                  <th>Date</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {fetchingRecords ? (
                  <tr>
                    <td colSpan="3" style={{ textAlign: 'center' }}>Loading...</td>
                  </tr>
                ) : pastRecords.length === 0 ? (
                  <tr>
                    <td colSpan="3" style={{ textAlign: 'center' }}>No exam records found</td>
                  </tr>
                ) : pastRecords.map((record, idx) => (
                  <tr key={idx}>
                    <td>{recordPage * 5 + idx + 1}</td>
                    <td>{record.date}</td>
                    <td>{record.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="record-buttons">
              <button
                onClick={() => setRecordPage((prev) => Math.max(prev - 1, 0))}
                disabled={recordPage === 0 || fetchingRecords}
                style={{
                  padding: '8px 16px',
                  margin: '0 8px',
                  cursor: recordPage === 0 || fetchingRecords ? 'not-allowed' : 'pointer',
                  opacity: recordPage === 0 || fetchingRecords ? 0.5 : 1
                }}
              >
                Previous
              </button>
              <span style={{ margin: '0 16px' }}>
                Page {recordPage + 1} / {Math.max(1, Math.ceil(totalRecords / 5))}
              </span>
              <button
                onClick={() => setRecordPage((prev) => prev + 1)}
                disabled={(recordPage + 1) * 5 >= totalRecords || fetchingRecords}
                style={{
                  padding: '8px 16px',
                  margin: '0 8px',
                  cursor: (recordPage + 1) * 5 >= totalRecords || fetchingRecords ? 'not-allowed' : 'pointer',
                  opacity: (recordPage + 1) * 5 >= totalRecords || fetchingRecords ? 0.5 : 1
                }}
              >
                Next
              </button>
            </div>
          </div>

          {/* PIE CHART */}
          <div className="average-pie-chart">
            <div
              className="pie-circle"
              style={{
                background: `conic-gradient(#800080 0% ${averageMarks}%, #000000ff ${averageMarks}% 100%)`,
                color: "#efd6ffff", // Light purple for text inside pie
              }}
            >
              {averageMarks}%
            </div>
            <div className="pie-description">
              This pie chart represents your <strong>average marks</strong> across all attempted exams.
            </div>
          </div>

          {/* LAST 5 EXAMS PERFORMANCE */}
          <div className="last-5-exams">
            <h3>Last 5 Exams Performance</h3>
            <div className="bar-chart">
              <div className="y-axis">
                {[100, 75, 50, 25, 0].map((val, idx) => (
                  <span key={idx}>{val}%</span>
                ))}
              </div>
              <div className="bars">
                {last5Exams.map((exam, idx) => (
                  <div key={idx} className="bar-wrapper">
                    <div
                      className="bar"
                      style={{ height: `${exam.percentage * 1.5}px` }}
                    >
                      <span className="bar-value">{exam.percentage}%</span>
                    </div>
                    <div className="bar-label">{exam.day}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* LEADERBOARD */}
          <div className="leaderboard">
            <h3>Leaderboard (Top 5)</h3>
            <table>
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Name</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((item) => (
                  <tr key={item.rank}>
                    <td>{item.rank}</td>
                    <td>{item.name}</td>
                    <td>{item.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Dashboard;
