import React from 'react';
import { Link } from 'react-router-dom';

const Home = () => {
  const cardStyle = {
    backgroundColor: '#145A32',
    width: '220px',
    padding: '30px',
    borderRadius: '10px',
    boxShadow: '0 4px 10px rgba(0,0,0,0.3)',
    color: 'white',
    fontWeight: 'bold',
    fontSize: '20px',
    transition: 'transform 0.3s, background-color 0.3s',
    cursor: 'pointer',
  };

  const hoverStyle = {
    transform: 'scale(1.08)',
    backgroundColor: '#1E8449',
  };

  return (
    <div style={{ fontFamily: "'Poppins', sans-serif" }}>
      
      {/* ---------- NAVBAR ---------- */}
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        backgroundColor: '#0B3D02', // darker green
        padding: '15px 50px',
        color: 'white',
        boxShadow: '0 2px 5px rgba(0,0,0,0.3)'
      }}>
        <h2 style={{ margin: 0 }}>Aptitude Preparation</h2>
        <button 
          style={{
            backgroundColor: 'white',
            color: '#0B3D02',
            border: 'none',
            padding: '8px 18px',
            borderRadius: '5px',
            cursor: 'pointer',
            fontWeight: 'bold'
          }}
        >
          Login
        </button>
      </nav>

      {/* ---------- INTRO SECTION ---------- */}
      <section style={{
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: '60px 100px',
        backgroundColor: '#F4F7F4'
      }}>
        <img 
          src="/home.jpg" 
          alt="Aptitude Preparation" 
          style={{
            width: '350px',
            borderRadius: '10px',
            marginRight: '50px',
            boxShadow: '0 4px 10px rgba(0,0,0,0.3)'
          }}
        />
        <p style={{
          fontSize: '22px',
          lineHeight: '1.8',
          color: '#222',
          maxWidth: '550px',
          textAlign: 'justify',
          fontFamily: "'Segoe UI', sans-serif"
        }}>
          Welcome to the <b>Aptitude Preparation</b> platform!  
          Here, you can sharpen your reasoning, quantitative, and verbal skills 
          through well-structured practice sets.  
          Prepare confidently for your placement exams and boost your problem-solving 
          potential with focused learning.
        </p>
      </section>

      {/* ---------- SYLLABUS SECTION ---------- */}
      <section style={{ 
        padding: '50px 100px', 
        textAlign: 'center',
        backgroundColor: '#0B3D02'
      }}>
        <h2 style={{ color: 'white', marginBottom: '40px' }}>Syllabus</h2>
        
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '40px',
          flexWrap: 'wrap'
        }}>
          {['Reasoning', 'Quantitative', 'Verbal'].map((topic, index) => (
            <div 
              key={index}
              style={cardStyle}
              onMouseEnter={e => Object.assign(e.currentTarget.style, hoverStyle)}
              onMouseLeave={e => Object.assign(e.currentTarget.style, cardStyle)}
            >
              {topic}
            </div>
          ))}
        </div>
      </section>

      {/* ---------- FOOTER ---------- */}
      <footer style={{
        backgroundColor: '#0B3D02',
        color: 'white',
        textAlign: 'center',
        padding: '20px 10px',
        fontSize: '16px',
        boxShadow: '0 -2px 5px rgba(0,0,0,0.3)',
        marginTop: '50px'
      }}>
        © {new Date().getFullYear()} Aptitude Preparation | Designed with ❤️ by Shamal
      </footer>

    </div>
  );
};

export default Home;
