import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./CommonUI";

const Home = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [counters, setCounters] = useState([0, 0, 0]); // animated stats counters

  const syllabus = [
    {
      title: "Reasoning",
      topics: ["Blood Relation", "Coding-Decoding", "Series", "Puzzles"],
    },
    {
      title: "Quantitative",
      topics: ["Percentages", "Profit & Loss", "Time & Work", "Averages"],
    },
    {
      title: "Verbal",
      topics: ["Synonyms", "Antonyms", "Reading Comprehension", "Grammar"],
    },
  ];

  const stats = [
    { label: "Questions", value: 500 },
    { label: "Practice Sets", value: 50 },
    { label: "Students Benefited", value: 1000 },
  ];

  // Scroll animations for fade-up
  useEffect(() => {
    const handleScroll = () => {
      const fadeSections = document.querySelectorAll(".fade-up");
      fadeSections.forEach((el) => {
        const rect = el.getBoundingClientRect();
        if (rect.top < window.innerHeight - 100) {
          el.style.opacity = 1;
          el.style.transform = "translateY(0)";
        }
      });
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  // Animate counters
  useEffect(() => {
    let start = [0, 0, 0];
    const end = stats.map((s) => s.value);
    const duration = 2000;
    const stepTime = 30;
    let interval = setInterval(() => {
      let done = true;
      start = start.map((val, i) => {
        if (val < end[i]) {
          done = false;
          return Math.min(val + Math.ceil(end[i] / (duration / stepTime)), end[i]);
        } else return val;
      });
      setCounters([...start]);
      if (done) clearInterval(interval);
    }, stepTime);
  }, []);

  const toggleDarkMode = () => setIsDarkMode(!isDarkMode);

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        backgroundColor: isDarkMode ? "#121212" : "#fff",
        color: isDarkMode ? "#f1f1f1" : "#222",
        transition: "all 0.3s",
      }}
    >
      {/* NAVBAR */}
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={false} // Home page: user not logged in
      />

      {/* INTRO SECTION */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          padding: "60px 100px",
          backgroundColor: isDarkMode ? "#1E1E1E" : "#F4F7F4",
          borderRadius: "10px",
          margin: "20px 50px",
        }}
      >
        <img
          src="/home.jpg"
          alt="Aptitude Preparation"
          className="hero-img"
          style={{
            width: "350px",
            borderRadius: "10px",
            marginRight: "20px",
            boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
            transition: "transform 0.5s ease",
          }}
        />
        <p
          style={{
            fontSize: "22px",
            lineHeight: "1.8",
            maxWidth: "550px",
            textAlign: "justify",
            fontFamily: "'Segoe UI', sans-serif",
          }}
        >
          Welcome to the <b>Aptitude Preparation</b> platform! Here, you can
          sharpen your reasoning, quantitative, and verbal skills through
          well-structured practice sets. Prepare confidently for your placement
          exams and boost your problem-solving potential with focused learning.
        </p>
      </section>

      {/* SYLLABUS SECTION */}
      <section
        className="fade-up"
        style={{
          padding: "50px 100px",
          textAlign: "center",
          backgroundColor: "#0B3D02",
        }}
      >
        <h2 style={{ color: "white", marginBottom: "40px" }}>Syllabus</h2>
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            gap: "40px",
            flexWrap: "wrap",
          }}
        >
          {syllabus.map((item, index) => (
            <div
              key={index}
              className="card"
              style={{
                backgroundColor: "#145A32",
                width: "220px",
                padding: "30px",
                borderRadius: "10px",
                boxShadow: "0 4px 10px rgba(0,0,0,0.3)",
                color: "white",
                fontWeight: "bold",
                fontSize: "20px",
                transition: "all 0.3s ease",
                cursor: "pointer",
                position: "relative",
                overflow: "hidden",
              }}
              onMouseEnter={(e) => {
                const topicsDiv = e.currentTarget.querySelector(".topics");
                if (topicsDiv) {
                  topicsDiv.style.opacity = 1;
                  topicsDiv.style.maxHeight = "200px";
                  const topicItems = topicsDiv.querySelectorAll(".topic-item");
                  topicItems.forEach((item) => {
                    item.style.opacity = 1;
                    item.style.transform = "translateX(0)";
                  });
                }
              }}
              onMouseLeave={(e) => {
                const topicsDiv = e.currentTarget.querySelector(".topics");
                if (topicsDiv) {
                  topicsDiv.style.opacity = 0;
                  topicsDiv.style.maxHeight = 0;
                  const topicItems = topicsDiv.querySelectorAll(".topic-item");
                  topicItems.forEach((item) => {
                    item.style.opacity = 0;
                    item.style.transform = "translateX(-20px)";
                  });
                }
              }}
            >
              {item.title}
              <div
                className="topics"
                style={{
                  marginTop: "10px",
                  opacity: 0,
                  maxHeight: 0,
                  transition: "all 0.5s ease",
                  overflow: "hidden",
                  fontSize: "15px",
                  textAlign: "left",
                  color: "#e8f5e9",
                }}
              >
                {item.topics.map((topic, i) => (
                  <div
                    key={i}
                    className="topic-item"
                    style={{
                      margin: "5px 0",
                      paddingLeft: "10px",
                      display: "flex",
                      alignItems: "center",
                      opacity: 0,
                      transform: "translateX(-20px)",
                      transition: "all 0.4s ease",
                    }}
                  >
                    {topic}
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* STATISTICS SECTION */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "60px",
          padding: "50px",
          textAlign: "center",
          backgroundColor: isDarkMode ? "#222" : "#F4F7F4",
        }}
      >
        {stats.map((s, index) => (
          <div key={index} style={{ fontSize: "20px", fontWeight: "bold" }}>
            <div style={{ fontSize: "32px", color: "#0B3D02" }}>
              {counters[index]}+
            </div>
            <div>{s.label}</div>
          </div>
        ))}
      </section>

      {/* SOCIAL MEDIA SECTION */}
      <section
        className="fade-up"
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "30px",
          padding: "30px 0",
          backgroundColor: isDarkMode ? "#111" : "#F4F7F4",
        }}
      >
        <div
          className="social-icons"
          style={{ display: "flex", gap: "20px", alignItems: "center" }}
        >
          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "8px",
              cursor: "pointer",
            }}
          >
            <span
              style={{
                fontWeight: "bold",
                color: isDarkMode ? "#f1f1f1" : "#222",
              }}
            >
              Instagram
            </span>
            <img
              src="/instagram.jpg"
              alt="Instagram"
              style={{ width: "40px", transition: "transform 0.3s, filter 0.3s" }}
            />
          </div>

          <div
            style={{
              display: "flex",
              alignItems: "center",
              gap: "8px",
              cursor: "pointer",
            }}
          >
            <span
              style={{
                fontWeight: "bold",
                color: isDarkMode ? "#f1f1f1" : "#222",
              }}
            >
              YouTube
            </span>
            <img
              src="/youtube.jpg"
              alt="YouTube"
              style={{ width: "40px", transition: "transform 0.3s, filter 0.3s" }}
            />
          </div>
        </div>
      </section>

      {/* FOOTER */}
      <Footer />
    </div>
  );
};

export default Home;
