import React, { useState } from "react";
import { Navbar, Footer } from "./CommonUI"; // your existing Navbar and Footer

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [currentSlide, setCurrentSlide] = useState(0);

  const slides = [
    "Welcome to Admin Dashboard",
    "Manage Users",
    "View Reports",
    "Settings",
  ];

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  const nextSlide = () => setCurrentSlide((currentSlide + 1) % slides.length);
  const prevSlide = () =>
    setCurrentSlide((currentSlide - 1 + slides.length) % slides.length);

  return (
    <div style={{ display: "flex", minHeight: "100vh" }}>
      
      {/* Sidebar */}
      <div
        style={{
          width: sidebarOpen ? "220px" : "60px",
          transition: "width 0.3s",
          backgroundColor: "#4b0082",
          color: "white",
          padding: "15px",
          boxSizing: "border-box",
        }}
      >
        <button
          onClick={toggleSidebar}
          style={{
            backgroundColor: "#6a0dad",
            border: "none",
            color: "white",
            padding: "10px",
            width: "100%",
            cursor: "pointer",
            marginBottom: "20px",
          }}
        >
          {sidebarOpen ? "Close" : "≡"}
        </button>

        {sidebarOpen && (
          <div>
            <h4>Profile</h4>
            <p><strong>Name:</strong> Admin Name</p>
            <p><strong>Email:</strong> admin@example.com</p>
            <p><strong>Role:</strong> Admin</p>
          </div>
        )}
      </div>

      {/* Main Content */}
      <div style={{ flex: 1, padding: "20px" }}>
        <Navbar />

        {/* Slider */}
        <div
          style={{
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            margin: "30px 0",
          }}
        >
          <button
            onClick={prevSlide}
            style={{
              backgroundColor: "#6a0dad",
              color: "white",
              border: "none",
              padding: "10px 15px",
              marginRight: "15px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            ◀
          </button>

          <div
            style={{
              backgroundColor: "#dcd6f7",
              color: "#4b0082",
              padding: "40px",
              minWidth: "300px",
              borderRadius: "10px",
              textAlign: "center",
              fontSize: "1.2rem",
            }}
          >
            {slides[currentSlide]}
          </div>

          <button
            onClick={nextSlide}
            style={{
              backgroundColor: "#6a0dad",
              color: "white",
              border: "none",
              padding: "10px 15px",
              marginLeft: "15px",
              borderRadius: "5px",
              cursor: "pointer",
            }}
          >
            ▶
          </button>
        </div>

        <h1>Admin Dashboard</h1>

        <Footer />
      </div>
    </div>
  );
};

export default AdminDashboard;
