import React, { useState, useEffect } from "react";
import { Navbar, Footer } from "./CommonUI";
import "../css/admindashboard.css";
import AdminManage from "./AdminManage";

const AdminDashboard = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(
    localStorage.getItem("theme") === "dark" // âœ… persist theme
  );
  const [searchTerm, setSearchTerm] = useState("");

  const isLoggedIn = true;

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // âœ… Toggle and persist theme
  const toggleDarkMode = () => {
    setIsDarkMode((prev) => {
      const newTheme = !prev ? "dark" : "light";
      localStorage.setItem("theme", newTheme);
      return !prev;
    });
  };

  // âœ… Apply dark mode class to body
  useEffect(() => {
    if (isDarkMode) {
      document.body.classList.add("dark-mode");
    } else {
      document.body.classList.remove("dark-mode");
    }
  }, [isDarkMode]);

  // Dummy data
  const totalUsers = 1200;
  const totalQuestions = 350;

  const dummyQuestions = [
    {
      id: 1,
      question: "What is 5 + 3?",
      options: ["6", "7", "8", "9"],
      correct: "8",
      category: "Quantitative",
    },
    {
      id: 2,
      question: "Which word is opposite of 'Happy'?",
      options: ["Sad", "Joyful", "Glad", "Pleased"],
      correct: "Sad",
      category: "Verbal",
    },
    {
      id: 3,
      question: "Which planet is known as the Red Planet?",
      options: ["Venus", "Earth", "Mars", "Jupiter"],
      correct: "Mars",
      category: "GK",
    },
    {
      id: 4,
      question: "Find the next number: 2, 4, 8, 16, ?",
      options: ["18", "20", "24", "32"],
      correct: "32",
      category: "Logical",
    },
    {
      id: 5,
      question: "What is the capital of Japan?",
      options: ["Seoul", "Beijing", "Tokyo", "Bangkok"],
      correct: "Tokyo",
      category: "GK",
    },
  ];

  const dummyUsers = [
    { id: 1, name: "John Doe", email: "john@example.com", role: "Student" },
    { id: 2, name: "Jane Smith", email: "jane@example.com", role: "Student" },
    { id: 3, name: "Alice Johnson", email: "alice@example.com", role: "Student" },
    { id: 4, name: "Bob Brown", email: "bob@example.com", role: "Student" },
    { id: 5, name: "Charlie Davis", email: "charlie@example.com", role: "Student" },
  ];

  const filteredUsers = dummyUsers.filter(
    (user) =>
      user.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      user.email.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className={`admin-dashboard ${isDarkMode ? "dark" : "light"}`}>
      {/* Navbar */}
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={isLoggedIn}
      />

      <div className="main-section">
        {/* Sidebar */}
        <div className={`sidebar ${sidebarOpen ? "open" : ""}`}>
          <button className="sidebar-toggle" onClick={toggleSidebar}>
            {sidebarOpen ? "Close" : "â‰¡"}
          </button>

          {sidebarOpen && (
            <div className="sidebar-content">
              <h4>Profile</h4>
              <p><strong>Name:</strong> Admin Name</p>
              <p><strong>Email:</strong> admin@example.com</p>
              <p><strong>Role:</strong> Admin</p>
              <button
                className="manage-admin-btn"
                onClick={() => (window.location.href = "/AdminManage")}
              >
                MANAGE ADMIN
              </button>
            </div>
          )}
        </div>

        {/* Dashboard Content */}
        <div className="dashboard-content">
          <h1>Admin Dashboard</h1>
          <p>Welcome, Admin! Here you can manage users, view reports, and handle system settings.</p>

          {/* Overview */}
          <section className="overview-section">
            <h2>Overview Dashboard</h2>
            <div className="overview-cards">
              <div className="card">
                <h3>Total Users Registered</h3>
                <p>{totalUsers}</p>
              </div>
              <div className="card">
                <h3>Total Questions</h3>
                <p>{totalQuestions}</p>
              </div>
            </div>
          </section>

          {/* Question Management */}
          <section className="management-section">
            <h2>Question Management</h2>
            <div className="table-wrapper">
              <table className="question-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Question</th>
                    <th>Option A</th>
                    <th>Option B</th>
                    <th>Option C</th>
                    <th>Option D</th>
                    <th>Correct</th>
                    <th>Category</th>
                    <th>Edit</th>
                    <th>Delete</th>
                  </tr>
                </thead>
                <tbody>
                  {dummyQuestions.map((q, index) => (
                    <tr key={q.id}>
                      <td>{index + 1}</td>
                      <td>{q.question}</td>
                      <td>{q.options[0]}</td>
                      <td>{q.options[1]}</td>
                      <td>{q.options[2]}</td>
                      <td>{q.options[3]}</td>
                      <td>{q.correct}</td>
                      <td>{q.category}</td>
                      <td><button className="edit-btn">Edit</button></td>
                      <td><button className="delete-btn">Delete</button></td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </section>

          {/* ðŸ‘¥ User Management */}
          <section className="management-section" style={{ marginTop: "40px" }}>
            <h2>User Management</h2>

            <div style={{ marginBottom: "15px" }}>
              <input
                type="text"
                placeholder="Search by name or email"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                style={{
                  padding: "8px",
                  width: "300px",
                  borderRadius: "4px",
                  border: "1px solid #ccc",
                }}
              />
            </div>

            <div className="table-wrapper">
              <table className="question-table">
                <thead>
                  <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Role</th>
                    <th>View History</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredUsers.map((user, index) => (
                    <tr key={user.id}>
                      <td>{index + 1}</td>
                      <td>{user.name}</td>
                      <td>{user.email}</td>
                      <td>{user.role}</td>
                      <td>
                        <button
                          className="edit-btn"
                          onClick={() => alert(`Viewing history of ${user.name}`)}
                        >
                          View History
                        </button>
                      </td>
                    </tr>
                  ))}
                  {filteredUsers.length === 0 && (
                    <tr>
                      <td colSpan="5" style={{ textAlign: "center", padding: "10px" }}>
                        No users found.
                      </td>
                    </tr>
                  )}
                </tbody>
              </table>
            </div>
          </section>
        </div>
      </div>

      {/* Footer */}
      <Footer />
    </div>
  );
};

export default AdminDashboard;
