import React, { useState } from "react";
import { Navbar, Footer } from "./CommonUI";

const Dashboard = ({ isDarkMode, toggleDarkMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        minHeight: "100vh",
        backgroundColor: isDarkMode ? "#121212" : "#F4F7F4",
        transition: "all 0.3s",
      }}
    >
      {/* ---------- NAVBAR ---------- */}
      <Navbar isDarkMode={isDarkMode} toggleDarkMode={toggleDarkMode} isLoggedIn={true} />

      <div style={{ display: "flex" }}>
        {/* ---------- SIDEBAR ---------- */}
        <div
          style={{
            width: sidebarOpen ? "200px" : "40px",
            backgroundColor: isDarkMode ? "#1E1E1E" : "#0B3D02",
            color: "white",
            transition: "width 0.3s",
            padding: "20px 10px",
            display: "flex",
            flexDirection: "column",
            alignItems: sidebarOpen ? "flex-start" : "center",
            borderRight: isDarkMode ? "1px solid #333" : "1px solid #145A32",
            minHeight: "calc(100vh - 60px)", // adjust for navbar height
          }}
        >
          <button
            onClick={toggleSidebar}
            style={{
              backgroundColor: sidebarOpen ? "#145A32" : isDarkMode ? "#333" : "#145A32",
              color: "white",
              border: "none",
              padding: "8px",
              borderRadius: "5px",
              cursor: "pointer",
              marginBottom: "20px",
              width: sidebarOpen ? "100%" : "35px",
              transition: "all 0.3s",
            }}
          >
            {sidebarOpen ? "Close" : "≡"}
          </button>

          {sidebarOpen && (
            <div style={{ marginTop: "20px", fontSize: "14px" }}>
              <div style={{ marginBottom: "15px", fontWeight: "bold" }}>Profile</div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Name:</strong> John Doe
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Email:</strong> john.doe@example.com
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Role:</strong> Student
              </div>
            </div>
          )}
        </div>

        {/* ---------- MAIN CONTENT ---------- */}
        <div style={{ flex: 1, padding: "30px" }}>
          <h2 style={{ color: isDarkMode ? "#f1f1f1" : "#0B3D02" }}>Dashboard</h2>
          <p style={{ color: isDarkMode ? "#ccc" : "#222" }}>
            Welcome to your dashboard! You can manage your profile, view statistics, and navigate through the platform using the sidebar.
          </p>
        </div>
      </div>

      {/* ---------- FOOTER ---------- */}
      <Footer />
    </div>
  );
};

export default Dashboard;
