import React, { useState } from "react";
import "../css/exam.css";


const Exam = () => {
  const [showInstructions, setShowInstructions] = useState(true);
  const [showQuestions, setShowQuestions] = useState(false);
  const [showResult, setShowResult] = useState(false);

  const dummyQuestions = [
    { question: "What is 2 + 2?", options: ["2", "3", "4", "5"] },
    { question: "Which is a programming language?", options: ["HTML", "CSS", "Python", "Photoshop"] },
    { question: "What is the capital of India?", options: ["Delhi", "Mumbai", "Kolkata", "Chennai"] },
    { question: "What color is the sky?", options: ["Blue", "Green", "Red", "Yellow"] },
    { question: "Which one is a fruit?", options: ["Carrot", "Apple", "Potato", "Onion"] },
    { question: "2 * 3 equals?", options: ["5", "6", "7", "8"] },
    { question: "Which is not a mammal?", options: ["Dog", "Cat", "Dolphin", "Shark"] },
    { question: "React is a ?", options: ["Library", "Database", "Server", "Language"] },
    { question: "Sun rises in?", options: ["West", "East", "North", "South"] },
    { question: "HTML stands for?", options: ["HyperText Markup Language", "HighText Machine Language", "Hyper Tool Multi Language", "None"] },
  ];

  return (
    <div className="exam-container">
      {/* Instructions Pop-Up */}
      {showInstructions && (
        <div className="popup instructions-popup">
          <h2>Exam Instructions</h2>
          <ul>
            <li>Total 10 questions.</li>
            <li>Each question has 4 options.</li>
            <li>You have 30 seconds to complete the exam.</li>
            <li>Do not refresh the page during the exam.</li>
          </ul>
          <button className="ok-btn" onClick={() => { setShowInstructions(false); setShowQuestions(true); }}>OK</button>
        </div>
      )}

      {/* Questions Section */}
      {showQuestions && (
        <div className="questions-section">
          <div className="timer">Time Left: 00:30</div>
          {dummyQuestions.map((q, idx) => (
            <div className="question-card" key={idx}>
              <h3>{idx + 1}. {q.question}</h3>
              <div className="options">
                {q.options.map((opt, i) => (
                  <button className="option-btn" key={i}>{opt}</button>
                ))}
              </div>
            </div>
          ))}
        </div>
      )}

      {/* Result Pop-Up */}
      {showResult && (
        <div className="popup result-popup">
          <h2>Exam Completed!</h2>
          <p>Your Score: 8/10</p>
          <div className="result-buttons">
            <button className="reset-btn" onClick={() => { setShowQuestions(true); setShowResult(false); }}>Reset</button>
            <button className="home-btn" onClick={() => { window.location.href = "/"; }}>Home</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Exam;
