import React, { useState } from "react";
import { Navbar, Footer } from "./CommonUI";

const Dashboard = ({ isDarkMode, toggleDarkMode }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [recordPage, setRecordPage] = useState(0);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);

  // Dummy Data
  const leaderboard = [
    { rank: 1, name: "Alice", score: "95%" },
    { rank: 2, name: "Bob", score: "90%" },
    { rank: 3, name: "Charlie", score: "85%" },
    { rank: 4, name: "David", score: "82%" },
    { rank: 5, name: "Eve", score: "80%" },
  ];

  const pastRecords = [
    { date: "01-10-2025", score: "80%" },
    { date: "05-10-2025", score: "75%" },
    { date: "10-10-2025", score: "82%" },
    { date: "15-10-2025", score: "78%" },
    { date: "20-10-2025", score: "88%" },
    { date: "25-10-2025", score: "85%" },
    { date: "30-10-2025", score: "90%" },
    { date: "04-11-2025", score: "92%" },
    { date: "09-11-2025", score: "87%" },
    { date: "14-11-2025", score: "89%" },
  ];

  const last5Exams = [
    { exam: "Mock 1", percentage: 80 },
    { exam: "Mock 2", percentage: 75 },
    { exam: "Mock 3", percentage: 82 },
    { exam: "Mock 4", percentage: 78 },
    { exam: "Mock 5", percentage: 88 },
  ];

  const blueShade = "#1E90FF"; // Blue color for cards / buttons

  return (
    <div
      style={{
        fontFamily: "'Poppins', sans-serif",
        minHeight: "100vh",
        backgroundColor: "#ffffff", // Main white background
        transition: "all 0.3s",
      }}
    >
      {/* ---------- NAVBAR ---------- */}
      <Navbar
        isDarkMode={isDarkMode}
        toggleDarkMode={toggleDarkMode}
        isLoggedIn={true}
        navbarStyle={{
          backgroundColor: "#06400F", // Dark green sidebar
          color: "white",
          boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
          position: "relative",
        }}
      />

      {/* ---------- Start Exam Button ---------- */}
      <div style={{ display: "flex", justifyContent: "flex-end", padding: "15px 30px" }}>
        <button
          style={{
            backgroundColor: blueShade,
            color: "white",
            border: "none",
            padding: "10px 20px",
            borderRadius: "8px",
            cursor: "pointer",
            fontWeight: "bold",
            boxShadow: "0 2px 5px rgba(0,0,0,0.2)",
            transition: "all 0.3s",
          }}
          onMouseEnter={e => (e.target.style.backgroundColor = "#1C86EE")}
          onMouseLeave={e => (e.target.style.backgroundColor = blueShade)}
        >
          Start Exam
        </button>
      </div>

      <div style={{ display: "flex" }}>
        {/* ---------- SIDEBAR ---------- */}
        <div
          style={{
            width: sidebarOpen ? "220px" : "40px",
            backgroundColor: "#06400F",
            color: "white",
            transition: "width 0.3s",
            padding: "20px 10px",
            display: "flex",
            flexDirection: "column",
            alignItems: sidebarOpen ? "flex-start" : "center",
            borderRight: "1px solid #0B3D02",
            minHeight: "calc(100vh - 60px)",
          }}
        >
          <button
            onClick={toggleSidebar}
            style={{
              backgroundColor: sidebarOpen ? "#0B3D02" : "#06400F",
              color: "white",
              border: "none",
              padding: "8px",
              borderRadius: "5px",
              cursor: "pointer",
              marginBottom: "20px",
              width: sidebarOpen ? "100%" : "35px",
              transition: "all 0.3s",
            }}
          >
            {sidebarOpen ? "Close" : "≡"}
          </button>

          {sidebarOpen && (
            <div style={{ marginTop: "20px", fontSize: "14px" }}>
              <div style={{ marginBottom: "15px", fontWeight: "bold" }}>Profile</div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Name:</strong> John Doe
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Email:</strong> john.doe@example.com
              </div>
              <div style={{ marginBottom: "10px" }}>
                <strong>Role:</strong> Student
              </div>
            </div>
          )}
        </div>

        {/* ---------- MAIN CONTENT ---------- */}
        <div style={{ flex: 1, padding: "30px", color: "#06400F" }}>
          <h2>Dashboard</h2>
          <p>Welcome to your dashboard! Track your exams, scores, and progress.</p>

          {/* ---------- Summary Cards ---------- */}
          <div style={{ display: "flex", gap: "20px", marginTop: "20px" }}>
            {[
              { title: "Total Attempts", value: 10 },
              { title: "Average Marks", value: "78%" },
              { title: "Position", value: "3rd" },
            ].map((card, idx) => (
              <div
                key={idx}
                style={{
                  flex: 1,
                  backgroundColor: blueShade,
                  padding: "20px",
                  borderRadius: "10px",
                  boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
                  textAlign: "center",
                  color: "white",
                  cursor: "pointer",
                  transition: "all 0.3s",
                }}
                onMouseEnter={e => {
                  e.currentTarget.style.transform = "translateY(-5px)";
                  e.currentTarget.style.boxShadow = "0 6px 12px rgba(0,0,0,0.3)";
                }}
                onMouseLeave={e => {
                  e.currentTarget.style.transform = "translateY(0)";
                  e.currentTarget.style.boxShadow = "0 2px 6px rgba(0,0,0,0.2)";
                }}
              >
                <div style={{ fontSize: "18px", fontWeight: "bold" }}>{card.title}</div>
                <div style={{ fontSize: "28px", marginTop: "10px" }}>{card.value}</div>
              </div>
            ))}
          </div>

          {/* ---------- Graph for Last 5 Exams ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "10px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3>Last 5 Exams Performance</h3>
            <div style={{ display: "flex", gap: "20px", alignItems: "flex-end", height: "180px" }}>
              {last5Exams.map((exam, idx) => (
                <div key={idx} style={{ textAlign: "center", flex: 1 }}>
                  <div
                    style={{
                      height: `${exam.percentage * 1.5}px`,
                      backgroundColor: blueShade,
                      borderRadius: "5px 5px 0 0",
                      transition: "height 0.5s",
                    }}
                  ></div>
                  <div style={{ marginTop: "5px" }}>{exam.exam}</div>
                  <div>{exam.percentage}%</div>
                </div>
              ))}
            </div>
          </div>

          {/* ---------- Leaderboard ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "10px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3>Leaderboard (Top 5)</h3>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                color: "#06400F",
              }}
            >
              <thead>
                <tr>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>Rank</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>Name</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((user, idx) => (
                  <tr key={idx}>
                    <td style={{ padding: "10px" }}>{user.rank}</td>
                    <td style={{ padding: "10px" }}>{user.name}</td>
                    <td style={{ padding: "10px" }}>{user.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          {/* ---------- Past Records ---------- */}
          <div
            style={{
              marginTop: "40px",
              padding: "20px",
              border: "1px solid #0B3D02",
              borderRadius: "10px",
              backgroundColor: "#f0f8ff",
            }}
          >
            <h3>Past Exam Records</h3>
            <table
              style={{
                width: "100%",
                borderCollapse: "collapse",
                color: "#06400F",
              }}
            >
              <thead>
                <tr>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>#</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>Date</th>
                  <th style={{ borderBottom: "1px solid #0B3D02", padding: "10px" }}>Score</th>
                </tr>
              </thead>
              <tbody>
                {pastRecords.slice(recordPage * 5, recordPage * 5 + 5).map((record, idx) => (
                  <tr key={idx}>
                    <td style={{ padding: "10px" }}>{recordPage * 5 + idx + 1}</td>
                    <td style={{ padding: "10px" }}>{record.date}</td>
                    <td style={{ padding: "10px" }}>{record.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div style={{ marginTop: "10px", display: "flex", gap: "10px" }}>
              <button
                onClick={() => setRecordPage(Math.max(recordPage - 1, 0))}
                disabled={recordPage === 0}
                style={{ padding: "8px 12px", cursor: "pointer" }}
              >
                Back
              </button>
              <button
                onClick={() => setRecordPage(Math.min(recordPage + 1, Math.floor(pastRecords.length / 5)))}
                disabled={(recordPage + 1) * 5 >= pastRecords.length}
                style={{ padding: "8px 12px", cursor: "pointer" }}
              >
                Next
              </button>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </div>
  );
};

export default Dashboard;
